ENT.Base = 'base_brush'ENT.Type = 'brush'ENT.OnTrigger = {}ENT.NextMap = ''ENT.DisableTouch = falseENT.PreviousChapter = falsefunction ENT:Initialize()	local w = self.max.x - self.min.x	local l = self.max.y - self.min.y	local h = self.max.z - self.min.z			local min = Vector(0 - (w / 2), 0 - (l / 2), 0 - (h / 2))	local max = Vector(w / 2, l / 2, h / 2)		self:DrawShadow(false)	self:SetCollisionBounds(min, max)	self:SetSolid(SOLID_BBOX)	self:SetCollisionGroup(COLLISION_GROUP_WORLD)	self:SetMoveType(0)	self:SetTrigger(true)endfunction ENT:StartTouch(ent)	if ent and ent:IsValid() and ent:IsPlayer() and ent:Team() == TEAM_ALIVE then		ent:SetTeam(TEAM_COMPLETED_MAP)				if ent:GetVehicle() and ent:GetVehicle():IsValid() then			ent:GetVehicle():Remove()		end		ent:Lock()		ent:SetMoveType(0)		ent:SetAvoidPlayers(false)		PrintMessage(HUD_PRINTTALK, Format("%s completed the map (%s) [%i of %i].", ent:Nick(), string.ToMinutesSeconds(CurTime() - ent.startTime), team.NumPlayers(TEAM_COMPLETED_MAP), self.playersAlive))	endendfunction ENT:Think()	local currentMap = game.GetMap()	local hl2maps = {			"d1_trainstation_01",		"d1_trainstation_02",		"d1_trainstation_03",		"d1_trainstation_04",		"d1_trainstation_05",		"d1_trainstation_06",	}		self.playersAlive = team.NumPlayers(TEAM_ALIVE) + team.NumPlayers(TEAM_COMPLETED_MAP)		if self.playersAlive > 0 and team.NumPlayers(TEAM_COMPLETED_MAP) >= (self.playersAlive * (NEXT_MAP_PERCENT / 100)) then			endend